import { 
    ChangeEvent, 
    KeyboardEvent, 
    createContext, 
    useContext, 
    useReducer, 
    useState 
} from "react";
import { v1 } from "uuid";
import { TodoAppContext } from "./todo-app.context";
import { TodoInputContextTypes, TodoInputProviderTypes } from "./context-types";



export const TodoInputContext = createContext<TodoInputContextTypes>({
    value: '',
    error: false,
    inputHandler: () => null,
    addTaskHandler: () => null,
    onKeyPressHandler: () => null,
});

// export const INPUT_ACTION_TYPES = {
//     SET_INPUT_VALUE: 'SET_INPUT_VALUE',
//     SET_INPUT_ERROR: 'SET_INPUT_ERROR'
// }

// export const INITIAL_STATE = {
//     value: '',
//     error: false
// }

// const inputReducer = (state: any, action: any) => {
//     const { type, payload } = action;

//     switch(type) {
//         case INPUT_ACTION_TYPES.SET_INPUT_VALUE :
//         return {
//             ...state,
//             value: payload
//         }
//         case INPUT_ACTION_TYPES.SET_INPUT_ERROR :
//             return {
//                 ...state,
//                 error: !state.error
//             }
//             default: {
//                 throw new Error(`Unexpected type of ${type}`)
//             }
//     }
// }

const TodoInputProvider: React.FC<TodoInputProviderTypes> = ({children}) => {

    const { tasks, setTasks } = useContext(TodoAppContext);

    const [ error, setError ] = useState(false);
    const [ value, setValue ] = useState("");
    // const [ { error }, dispatch ] = useReducer(inputReducer, INITIAL_STATE);

    // const setValue = (value: string) => {
    //     dispatch({type: INPUT_ACTION_TYPES.SET_INPUT_VALUE, value: value})
    // }

    // const setError = (error: boolean) => {
    //     dispatch({type: INPUT_ACTION_TYPES.SET_INPUT_ERROR, error: error})
    // }


    const handelInput = (text: string) => {
            setValue(text);
        };

        const inputHandler = (e: ChangeEvent<HTMLInputElement>) => {
            handelInput(e.target.value);
        };

    const addTask = () => {
		const newTask = {
			id: v1(),
			title: value,
			isDone: false,
			isEditing: false,
		};

		if (newTask.title.trim() === "") {
			return;
		} else {
			let newTasks = [newTask, ...tasks];
			setTasks(newTasks);
			setValue("");
		}
	};

    const addTaskHandler = () => {
        if (value) {
            addTask();
        } else {
            setError(true);
        }
    };


	const onKeyPressHandler = (e: KeyboardEvent<HTMLInputElement>) => {
		setError(false);
		if (e.key === "Enter" && value) {
			addTask();
		}
	};

    const todoInputValue = {
        value,
        inputHandler,
        error,
        addTaskHandler,
        onKeyPressHandler
    }
    return (
        <TodoInputContext.Provider value={todoInputValue}>{children}</TodoInputContext.Provider>
    )

}

export default TodoInputProvider;
