import { createContext, useEffect, useState, useReducer } from "react";
import { TodoAppProviderType, TodoAppContextType } from "./context-types";
import { TaskType } from "../global-types";


export const TodoAppContext = createContext<TodoAppContextType>({
    tasks: [],
    filter: '',
    filteredTasks: [],
    setTasks: () => null,
    changeStatus: () => null,
    setFilter: () => null,
    setError: () => null,
    changeFilter: () => null,
    getLocalstorage: () => []
});

// type TodoAppReducerTypes = {
//     state: any;
//     action: any;
//     type: any;
//     payload: string;
// }

export const TODO_APP_ACTION_TYPE = {
    SET_TODO_APP_TYPE: 'SET_TODO_APP_TYPE'
}

const todoAppReducer = (state: any, action: any) => {
    const { type, payload } = action;

    switch(type) {
        case 'SET_TODO_APP_TYPE' :
            return {
                ...state,

                tasks: payload
            }
        default: 
        throw new Error(`Something went wrong in ${type}`)
    }
}

const INITIAL_STATE = {
    tasks: [],
    getLocalstorage: () => 'tasks'

}

const TodoAppProvider: React.FC<TodoAppProviderType> = ({children}) => {

    // const getLocalstorage = () => {
	// 	let taska = window.localStorage.getItem("tasks");

	// 	if(taska) {
	// 		return (taska = JSON.parse(localStorage.getItem('tasks') || '[]'));
	// 	} else { 
	// 		return [] 
	// 	}

	// }
    // const [tasks, setTasks] = useState(getLocalstorage());
    const [ { tasks }, dispatch ] = useReducer(todoAppReducer, INITIAL_STATE);

    useEffect(() => {
        let taska = window.localStorage.getItem("tasks");

		if(taska) {
			return (taska = JSON.parse(localStorage.getItem('tasks') || '[]'));
		} else { 
			return [] 
		}
    }, [])

    const setTasks = (tasks: TaskType[]) => {
        dispatch({type: TODO_APP_ACTION_TYPE.SET_TODO_APP_TYPE, payload: tasks});
    }

    useEffect(() => {
		if(tasks.length !== 0) {
			localStorage.setItem('tasks', JSON.stringify(tasks));
		} else {
			localStorage.setItem('tasks', JSON.stringify([]));
		}
		setTasks(tasks);

	}, [tasks]);

    const [ filter, setFilter ] = useState("all");
	const [ error, setError ] = useState(false);


    const changeStatus = (taskId: string, isDone: boolean) => {
        const task = tasks.find((t: any) => t.id === taskId);
            if (task) {
                task.isDone = !task.isDone;
            }
        setTasks([...tasks]);
    };


    let filteredTasks = tasks;
        if(filter === 'active') {
            filteredTasks = tasks.filter((t: any) => t.isDone === false);
		} else if (filter === 'completed') {
		    filteredTasks = tasks.filter((t: any) => t.isDone === true);
		}

		function changeFilter(value: string) {
			setFilter(value);
		}

    const contextValue = {
        tasks, 
        setTasks, 
        changeStatus,
        filter,
        setFilter,
        setError,
        changeFilter,
        filteredTasks,
        // getLocalstorage

    };

    return (
        <TodoAppContext.Provider value={contextValue}>{children}</TodoAppContext.Provider>
    )
}

export default TodoAppProvider;
