import { createContext, useContext, useState } from "react";
import { v1 } from "uuid";
import { TodoAppContext } from "./todo-app.context";

export const TodoInputContext = createContext({
    error: null,
    addTaskHandler: () => null,
    onKeyPressHandler: () => null,
});

const TodoInputProvider = ({children}) => {
    const { value, tasks, setTasks, setValue } = useContext(TodoAppContext);
    const [ error, setError ] = useState(null);

    const addTask = () => {
		const newTask = {
			id: v1(),
			title: value,
			isDone: false,
			isEditing: false,
		};

		if (newTask.title.trim() === "") {
			return;
		} else {
			let newTasks = [newTask, ...tasks];
			setTasks(newTasks);
			setValue("");
		}
	};

    const addTaskHandler = () => {
        if (value) {
            addTask();
        } else {
            setError(true);
        }
    };


	const onKeyPressHandler = (e) => {
		setError(null);
		if (e.key === "Enter" && value) {
			addTask();
		}
	};

    const todoInputValue = {
        value,
        error,
        addTaskHandler,
        onKeyPressHandler

    }
    return (
        <TodoInputContext.Provider value={todoInputValue}>{children}</TodoInputContext.Provider>
    )

}

export default TodoInputProvider;
