import { createContext, useEffect, useState } from "react";
import { v1 } from "uuid";

// type TodoAppProvider = {
//     children: any;
// }

export const TodoAppContext = createContext({
    tasks: [],
    setTasks: () => null,
    addTask: () => null,
    removeTask: (id) => null,
    value: '',
    setValue: () => null,
});

const TodoAppProvider = ({children}) => {
    const getLocalstorage = () => {
		let taska = window.localStorage.getItem("tasks");

		console.log(taska);
		if(taska) {
			return (taska = JSON.parse(localStorage.getItem('tasks') || '[]'));
		} else { 
			return [] 
		}

	}
    const [tasks, setTasks] = useState(getLocalstorage());

    useEffect(() => {
		if(tasks.length !== 0) {
			localStorage.setItem('tasks', JSON.stringify(tasks));
		} else {
			localStorage.setItem('tasks', JSON.stringify([]));
		}
		setTasks(tasks);
	}, [tasks]);


	const [ value, setValue ] = useState("");

    const addTask = () => {
		const newTask = {
			id: v1(),
			title: value,
			isDone: false,
			isEditing: false,
		};

		if (newTask.title.trim() === "") {
			return;
		} else {
			let newTasks = [newTask, ...tasks];
			setTasks(newTasks);
			setValue("");
		}
	};

	const removeTask = (id) => {
		const filteredTask = tasks.filter((t) => t.id !== id);
        if(window.confirm('Are you sure?')) {
            setTasks(filteredTask);
        }
	};

    const contextValue = {
        tasks, 
        setTasks, 
        addTask, 
        removeTask,
        value,
        setValue,
    };

    return (
        <TodoAppContext.Provider value={contextValue}>{children}</TodoAppContext.Provider>
    )
}

export default TodoAppProvider;
