import { createContext, useEffect, useState } from "react";
import { v1 } from "uuid";

// type TodoAppProvider = {
//     children: any;
// }

export const TodoAppContext = createContext({
    tasks: [],
    setTasks: () => null,
    addTask: () => null,
    removeTask: () => null,
    value: '',
    setValue: () => null,
    changeStatus: () => null,
    filter: 'all',
    setFilter: () => null,
    setError: () => null,
    changeFilter: () => null,
    filteredTasks: [],
    handelInput: () => null,
    addTaskHandler: () => null,
    onKeyPressHandler: () => null,
    inputHandler: () => null,
    editTodo: () => null,
});

const TodoAppProvider = ({children}) => {
    const getLocalstorage = () => {
		let taska = window.localStorage.getItem("tasks");

		console.log(taska);
		if(taska) {
			return (taska = JSON.parse(localStorage.getItem('tasks') || '[]'));
		} else { 
			return [] 
		}

	}
    const [tasks, setTasks] = useState(getLocalstorage());

    useEffect(() => {
		if(tasks.length !== 0) {
			localStorage.setItem('tasks', JSON.stringify(tasks));
		} else {
			localStorage.setItem('tasks', JSON.stringify([]));
		}
		setTasks(tasks);
	}, [tasks]);

    const [ filter, setFilter ] = useState("all");
	const [ error, setError ] = useState(false);



	const removeTask = (id) => {
		const filteredTask = tasks.filter((t) => t.id !== id);
        if(window.confirm('Are you sure?')) {
            setTasks(filteredTask);
        }
	};

    const changeStatus = (taskId, isDone) => {
		const task = tasks.find((t) => t.id === taskId);
            if (task) {
                task.isDone = !task.isDone;
            }
		setTasks([...tasks]);
	};

	const editTodo = (id, title) => {
		const editingTask = tasks.map((task) => ({
            ...task,
            title: task.id === id ? title : task.title
            })
	    );
		setTasks([...editingTask]);
    };

    let filteredTasks = tasks;
        if(filter === 'active') {
            filteredTasks = tasks.filter(t => t.isDone === false);
		} else if (filter === 'completed') {
		    filteredTasks = tasks.filter(t => t.isDone === true);
		}

		function changeFilter(value) {
			setFilter(value);
		}

    const contextValue = {
        tasks, 
        setTasks, 
        // addTask, 
        removeTask,
        // value,
        // setValue,
        changeStatus,
        filter,
        setFilter,
        error,
        setError,
        changeFilter,
        filteredTasks,
        // handelInput,
        // inputHandler,
        // addTaskHandler,
        // onKeyPressHandler,
        editTodo
    };

    return (
        <TodoAppContext.Provider value={contextValue}>{children}</TodoAppContext.Provider>
    )
}

export default TodoAppProvider;
