import React, { 
	// ChangeEvent, 
	// KeyboardEvent, 
	// useEffect, 
	useState,
	useContext,
} from 'react'

import { TodoAppContext } from '../../contexts/todo-app.context';
import TodoInput from '../todo-Input/TodoInput'
import ListOfTodos from '../list-of-todos/ListOfTodos';
import FilterButtons from '../buttons-components/filter-buttons/FilterButtons';

// import { FilterValuesType, TaskType } from '../../global-type';
import { v1 } from 'uuid';
import styled from './todo-app.module.css'

// type TodoAppPropsType = {};


const TodoApp = () => {

	const { tasks, setTasks } = useContext(TodoAppContext);


	const [ filter, setFilter ] = useState("all");
	const [ value, setValue ] = useState("");
	const [ error, setError ] = useState(null);

	const handelInput = (text) => {
		setValue(text);
	};

	const addTask = () => {
		const newTask = {
			id: v1(),
			title: value,
			isDone: false,
			isEditing: false,
		};

		if (newTask.title.trim() === "") {
			return;
		} else {
			let newTasks = [newTask, ...tasks];
			setTasks(newTasks);
			setValue("");
		}
	};

	const removeTask = (id) => {
		const filteredTask = tasks.filter((t) => t.id !== id);
        if(window.confirm('Are you sure?')) {
            setTasks(filteredTask);
        }
	};

	const changeStatus = (taskId, isDone) => {
		const task = tasks.find((t) => t.id === taskId);
            if (task) {
                task.isDone = !task.isDone;
            }
		setTasks([...tasks]);
	};


	const editTodo = (id, title) => {
		const editingTask = tasks.map((task) => ({
            ...task,
            title: task.id === id ? title : task.title
            })
	    );
		setTasks([...editingTask]);
    };

	const inputHandler = (e) => {
		handelInput(e.target.value);
	};

	const addTaskHandler = () => {
		if (value) {
			addTask();
		} else {
			setError(true);
		}
	};

	const onKeyPressHandler = (e) => {
		setError(null);
		if (e.key === "Enter" && value) {
			addTask();
		}
	};

        let filteredTasks = tasks;
        if(filter === 'active') {
            filteredTasks = tasks.filter(t => t.isDone === false);
		} else if (filter === 'completed') {
		    filteredTasks = tasks.filter(t => t.isDone === true);
		}

		function changeFilter(value) {
			setFilter(value);
		}

  return (
    <div className={styled.wrapper}>

        <h1 className={styled.title}>{"My Todo List"}</h1>

        <section className={styled.todoInputSec}>
            <TodoInput
				id={v1()}
				value={value}
				inputHandler={inputHandler}
				onKeyPressHandler={onKeyPressHandler}
				error={error}
				addTaskHandler={addTaskHandler}
				/>
        </section>

		<section className={styled.todosSec}>
			<div className={styled.todos}>
				{filteredTasks.map((taska) => {

					const onChangeHandler = (
						e
					) => {
						changeStatus(taska.id, e.currentTarget.checked);                    
					};


					return (
						<ListOfTodos 
							key={taska.id}
							title={taska.title}
							id={taska.id}
							isDone={taska.isDone}
							removeTask={() => removeTask(taska.id)} 
							onChange={onChangeHandler}
							editTodo={editTodo}
							onChangeHandler={onChangeHandler}
							checked={taska.isDone}
							onKeyPressHandler={onKeyPressHandler}
							filter={filter}
						/>
					)
				})}
			</div>
		</section>

        <FilterButtons 
			filter={filter}
			filteredTasks={filteredTasks}
			changeFilter={changeFilter}
			 />
    </div>
  )
}

export default TodoApp;
