export const enum TODOAPP_ACTION_TYPES  {
    SET_TODOAPP_TASKS,
    SET_TODOAPP_FILTER,
}

export const INITIAL_STATE = {
    filter: 'all',
    tasks: JSON.parse(window.localStorage.getItem('tasks') || '[]'),
}
// console.log(INITIAL_STATE.tasks);

type State = {
    filter: string,
    tasks: []
}

type Action = {
    type: TODOAPP_ACTION_TYPES,
    payload: any
}
//CREATE TYPES FOR STATE AND ACTION!!!!
export const todoAppReducer = (state: State, action: any) => {
    console.log(action);

    const { type, payload } = action;

    switch(type) {
        case TODOAPP_ACTION_TYPES.SET_TODOAPP_TASKS :
            return {
                ...state,
                tasks: payload
            }
        case TODOAPP_ACTION_TYPES.SET_TODOAPP_FILTER :
            return {
                ...state,
                filter: payload
            }
            default: {
                throw new Error(`Unexpected type of ${type}`)
            }
    }
}
