import { Dispatch, ReactNode, SetStateAction, createContext, useEffect, useState } from "react";
import { TaskType } from "../global-types";
import { log } from "console";

type TodoAppProviderType = {
    children: ReactNode;
    tasks: Array<TaskType>;
    setTasks: () => void;
    changeStatus: (taskId: string, isDone: boolean) => void;
    filter: string;
    setFilter: () => void;
    setError: () => void;
    filteredTasks: Array<TaskType>;
    changeFilter: (value: string) => void;
}

type TodoAppContextType = {
    tasks: Array<TaskType>;
    filter: string;
    setTasks: Dispatch<TaskType[]>;
    setFilter: Dispatch<string>;
    setError: Dispatch<boolean>;
    filteredTasks: Array<TaskType>;
    changeStatus: (taskId: string, isDone: boolean) => void;
    changeFilter: (value: string) => void;
}
export const TodoAppContext = createContext<TodoAppContextType>({
    tasks: [],
    filter: 'all',
    filteredTasks: [],
    setTasks: () => null,
    changeStatus: () => null,
    setFilter: () => null,
    setError: () => null,
    changeFilter: () => null
});

const TodoAppProvider: React.FC<TodoAppProviderType> = ({children}) => {

    const getLocalstorage = () => {
		let taska = window.localStorage.getItem("tasks");

		console.log(taska);
		if(taska) {
			return (taska = JSON.parse(localStorage.getItem('tasks') || '[]'));
		} else { 
			return [] 
		}

	}
    const [tasks, setTasks] = useState(getLocalstorage());

    useEffect(() => {
		if(tasks.length !== 0) {
			localStorage.setItem('tasks', JSON.stringify(tasks));
		} else {
			localStorage.setItem('tasks', JSON.stringify([]));
		}
		setTasks(tasks);
        console.log(setTasks(tasks));

	}, [tasks]);

    const [ filter, setFilter ] = useState("all");
	const [ error, setError ] = useState(false);


    const changeStatus = (taskId: string, isDone: boolean) => {
        const task = tasks.find((t: any) => t.id === taskId);
            if (task) {
                task.isDone = !task.isDone;
            }
        setTasks([...tasks]);
    };


    let filteredTasks = tasks;
        if(filter === 'active') {
            filteredTasks = tasks.filter((t: any) => t.isDone === false);
		} else if (filter === 'completed') {
		    filteredTasks = tasks.filter((t: any) => t.isDone === true);
		}

		function changeFilter(value: string) {
			setFilter(value);
		}

    const contextValue = {
        tasks, 
        setTasks, 
        changeStatus,
        filter,
        setFilter,
        setError,
        changeFilter,
        filteredTasks,
    };

    return (
        <TodoAppContext.Provider value={contextValue}>{children}</TodoAppContext.Provider>
    )
}

export default TodoAppProvider;
