import { createContext, useEffect, useState, useReducer } from "react";
import { TodoAppProviderType, TodoAppContextType } from "./context-types";


export const TodoAppContext = createContext<TodoAppContextType>({
    tasks: [],
    filter: '',
    filteredTasks: [],
    setTasks: () => null,
    changeStatus: () => null,
    setFilter: () => null,
    changeFilter: () => null
});

export const INPUT_ACTION_TYPES = {
    SET_TODOAPP_TASKS: 'SET_TODOAPP_TASKS',
    SET_TODOAPP_FILTER: 'SET_TODOAPP_FILTER',
}

export const INITIAL_STATE = {
    tasks: [],
    filter: 'all',
}

const todoAppReducer = (state: any, action: any) => {
    const { type, payload } = action;

    switch(type) {
        case INPUT_ACTION_TYPES.SET_TODOAPP_FILTER :
            return {
                filter: payload
            }
        case INPUT_ACTION_TYPES.SET_TODOAPP_TASKS :
            return {
                tasks: payload
            }
            default: {
                throw new Error(`Unexpected type of ${type}`)
            }
    }
}

const TodoAppProvider: React.FC<TodoAppProviderType> = ({children}) => {

    const [{filter, tasks}, dispatch] = useReducer(todoAppReducer, JSON.parse(localStorage.getItem('tasks') || '[]'))

    const setTasks = (taska: []) => {
        dispatch({type: INPUT_ACTION_TYPES.SET_TODOAPP_TASKS, payload: taska})
    }
    // const getLocalstorage = () => {
	// 	let taska = window.localStorage.getItem("tasks");

	// 	if(taska) {
	// 		return (taska = JSON.parse(localStorage.getItem('tasks') || '[]'));
	// 	} else { 
	// 		return [] 
	// 	}

	// }
    // const [tasks, setTasks] = useState(getLocalstorage());

    useEffect(() => {
		if(tasks.length !== 0) {
			localStorage.setItem('tasks', JSON.stringify(tasks));
		} else {
			localStorage.setItem('tasks', JSON.stringify([]));
		}
		setTasks(tasks);

	}, [tasks]);


    const setFilter = (values: string) => {
        dispatch({type: INPUT_ACTION_TYPES.SET_TODOAPP_FILTER, payload: values})
    }

    const changeStatus = (taskId: string, isDone: boolean) => {
        const task = tasks.find((t: any) => t.id === taskId);
            if (task) {
                task.isDone = !task.isDone;
            }
        setTasks([...tasks]);
    };


    let filteredTasks = tasks;
        if(filter === 'active') {
            filteredTasks = tasks.filter((t: any) => t.isDone === false);
		} else if (filter === 'completed') {
		    filteredTasks = tasks.filter((t: any) => t.isDone === true);
		}

		function changeFilter(value: string) {
			setFilter(value);
		}

    const contextValue = {
        tasks, 
        setTasks, 
        changeStatus,
        filter,
        setFilter,
        changeFilter,
        filteredTasks,
    };

    return (
        <TodoAppContext.Provider value={contextValue}>{children}</TodoAppContext.Provider>
    )
}

export default TodoAppProvider;
