import { TaskType } from "../global-types"


export const enum TODOAPP_ACTION_TYPES  {
    SET_TODOAPP_TASKS,
    SET_TODOAPP_FILTER,
}

export const INITIAL_STATE = {
    filter: 'all',
    tasks: JSON.parse(window.localStorage.getItem('tasks') || '[]'),
}
// console.log(INITIAL_STATE.tasks);

type State = {
    filter: string,
    tasks: []
}

type ActionTasks = {
    type: TODOAPP_ACTION_TYPES.SET_TODOAPP_TASKS
    payload: []
}
type ActionFilter = {
    type: TODOAPP_ACTION_TYPES.SET_TODOAPP_FILTER
    payload: string
}
type ActionType = ActionTasks | ActionFilter

export const todoAppReducer = (state: State, action: ActionType) => {

    const { type, payload } = action;

    switch(type) {
        case TODOAPP_ACTION_TYPES.SET_TODOAPP_TASKS :
            return {
                ...state,
                tasks: payload
            }
        case TODOAPP_ACTION_TYPES.SET_TODOAPP_FILTER :
            return {
                ...state,
                filter: payload
            }
            default: {
                throw new Error(`Unexpected type of ${type}`)
            }
    }
}
