export const enum INPUT_ACTION_TYPES  {
    SET_TODOAPP_TASKS,
    SET_TODOAPP_FILTER,
}

export const INITIAL_STATE = {
    filter: 'all',
    tasks: JSON.parse(window.localStorage.getItem('tasks') || '[]'),
}


//CREATE TYPES FOR STATE AND ACTION!!!!
export const todoAppReducer = (state: any, action: any) => {
    console.log(state);

    const { type, payload } = action;

    switch(type) {
        case INPUT_ACTION_TYPES.SET_TODOAPP_TASKS :
            return {
                ...state,
                tasks: payload
            }
        case INPUT_ACTION_TYPES.SET_TODOAPP_FILTER :
            return {
                ...state,
                filter: payload
            }
            default: {
                throw new Error(`Unexpected type of ${type}`)
            }
    }
}
