import { createContext, useEffect, useState } from "react";
import { v1 } from "uuid";

// type TodoAppProvider = {
//     children: any;
// }

export const TodoAppContext = createContext({
    tasks: [],
    setTasks: () => null,
    addTask: () => null,
    removeTask: () => null,
    value: '',
    setValue: () => null,
    changeStatus: () => null,
    filter: 'all',
    setFilter: () => null,
    error: null,
    setError: () => null,
    changeFilter: () => null,
    filteredTasks: [],
    handelInput: () => null,
    addTaskHandler: () => null,
    onKeyPressHandler: () => null,
    inputHandler: () => null,
});

const TodoAppProvider = ({children}) => {
    const getLocalstorage = () => {
		let taska = window.localStorage.getItem("tasks");

		console.log(taska);
		if(taska) {
			return (taska = JSON.parse(localStorage.getItem('tasks') || '[]'));
		} else { 
			return [] 
		}

	}
    const [tasks, setTasks] = useState(getLocalstorage());

    useEffect(() => {
		if(tasks.length !== 0) {
			localStorage.setItem('tasks', JSON.stringify(tasks));
		} else {
			localStorage.setItem('tasks', JSON.stringify([]));
		}
		setTasks(tasks);
	}, [tasks]);


	const [ value, setValue ] = useState("");
    const [ filter, setFilter ] = useState("all");
	const [ error, setError ] = useState(null);


    const handelInput = (text) => {
		setValue(text);
	};

	const inputHandler = (e) => {
		handelInput(e.target.value);
	};

    const addTask = () => {
		const newTask = {
			id: v1(),
			title: value,
			isDone: false,
			isEditing: false,
		};

		if (newTask.title.trim() === "") {
			return;
		} else {
			let newTasks = [newTask, ...tasks];
			setTasks(newTasks);
			setValue("");
		}
	};

    const addTaskHandler = () => {
        if (value) {
            addTask();
        } else {
            setError(true);
        }
    };


	const onKeyPressHandler = (e) => {
		setError(null);
		if (e.key === "Enter" && value) {
			addTask();
		}
	};

	const removeTask = (id) => {
		const filteredTask = tasks.filter((t) => t.id !== id);
        if(window.confirm('Are you sure?')) {
            setTasks(filteredTask);
        }
	};

    const changeStatus = (taskId, isDone) => {
		const task = tasks.find((t) => t.id === taskId);
            if (task) {
                task.isDone = !task.isDone;
            }
		setTasks([...tasks]);
	};

    let filteredTasks = tasks;
        if(filter === 'active') {
            filteredTasks = tasks.filter(t => t.isDone === false);
		} else if (filter === 'completed') {
		    filteredTasks = tasks.filter(t => t.isDone === true);
		}

		function changeFilter(value) {
			setFilter(value);
		}

    const contextValue = {
        tasks, 
        setTasks, 
        addTask, 
        removeTask,
        value,
        setValue,
        changeStatus,
        filter,
        setFilter,
        error,
        setError,
        changeFilter,
        filteredTasks,
        handelInput,
        addTaskHandler,
        onKeyPressHandler,
        inputHandler
    };

    return (
        <TodoAppContext.Provider value={contextValue}>{children}</TodoAppContext.Provider>
    )
}

export default TodoAppProvider;
