export const enum INPUT_ACTION_TYPES {
    SET_INPUT_VALUE,
    SET_INPUT_ERROR
}

export const INITIAL_STATE = {
    value: '',
    error: null
}

export const inputReducer = (state: any, action: any) => {
    const { type, payload } = action;

    switch(type) {
        case INPUT_ACTION_TYPES.SET_INPUT_VALUE :
        return {
            ...state,
            value: payload
        }
        case INPUT_ACTION_TYPES.SET_INPUT_ERROR :
            return {
                ...state,
                error: payload
            }
            default: {
                throw new Error(`Unexpected type of ${type}`)
            }
    }
}
